/// <reference types="react" />
import * as react_jsx_runtime from 'react/jsx-runtime';
import { Insight } from '@semoss/sdk';

/**
 * Space where to run commands in
 */
type Space = 'insight' | 'app' | 'system';

/**
 * Access the current insight
 */
declare const useInsight: () => {
    isInitialized: boolean;
    isAuthorized: boolean;
    isReady: boolean;
    error: Error | null;
    system: {
        config: {
            [key: string]: unknown;
            logins: Record<string, unknown>;
            availableProviders: {
                provider: string;
                name: string;
                isOauth: boolean;
            }[];
        };
    } | null;
    actions: {
        login: (credentials: {
            type: "native";
            username: string;
            password: string;
        } | {
            type: "oauth";
            provider: string;
        }) => Promise<boolean>;
        logout: () => Promise<boolean>;
        run: <O extends unknown[] | []>(pixel: string, space?: Space | undefined) => Promise<{
            pixelReturn: {
                isMeta: boolean;
                operationType: string[];
                output: O[number];
                pixelExpression: string;
                pixelId: string;
                additionalOutput?: unknown;
            }[];
        }>;
        runPy: <O_1>(python: string, space?: Space | undefined) => Promise<{
            output: O_1;
        }>;
        askModel: (engineId: string, command: string, space?: Space | undefined) => Promise<{
            output: {
                response: string;
            };
        }>;
        queryDatabase: (databaseId: string, query: string, options?: {
            collect: number;
        } | undefined, space?: Space | undefined) => Promise<{
            output: string | number | {
                values: unknown[][];
                headers: string[];
            };
        }>;
        upload: (files: File | File[], path: string, space?: Space | undefined) => Promise<{
            fileName: string;
            fileLocation: string;
        }[]>;
        download: (path: string | null, space?: Space | undefined) => Promise<boolean>;
    };
    insightId: string;
};

interface InsightProviderProps {
    /**
     * Content to render with the insight
     */
    children: React.ReactNode;
    /**
     * Options to load into the app
     */
    options?: Parameters<Insight['initialize']>[0];
}
declare const InsightProvider: (props: InsightProviderProps) => react_jsx_runtime.JSX.Element;

export { InsightProvider, useInsight };
